package Day2;

public class javamathincrementoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=50;
		int b=12;
		int Addition=a+b;
		int minus=a-b;
		int multiplication=a*b;
		int division=a/b;
		int remainder=a%b;
		
		System.out.println("This is addition in Java="+Addition);
		System.out.println("This is concatinating function="+Addition+880);
		System.out.println("This is subtraction in Java="+minus);
		System.out.println("This is product of 2 numbers="+multiplication);
		System.out.println("This is division in Java="+division);
		System.out.println("This is remainder in Java="+remainder);
		
		
		//increment and  decrement operator
				int i=80;
				i++;//increment operator
				i--;//decrement operator
				System.out.println(i++);
				System.out.println(i--);
				
				//Logical Operator with if(condition)
				
				int d=150;
				if(d>70) {
					System.out.println("The condition is true");
				}else {
					System.out.println("The condition is false");
				}
					
				}
				
				
				
				
	
	
		
		
		
		
		
		
		
		
		
		

	}


