package Redowork;

public class ArithmaticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int d=50;
		int s=6;
		int Addition=d+s+677;
		int minus=d-s;
		int multiplication=d*s;
		int division=d/s;
		int remainder=d%s;
		System.out.println("This is addition of two variables="+ Addition+888);
		//'+' is used as concatenating sign
		System.out.println("This is with concatenating sign="+ Addition);
		System.out.println("This is subtraction of two variables="+ minus);
		System.out.println("This is product of two variables="+ multiplication);
		System.out.println("This is division of two variables="+ division);
		System.out.println("This is remainder of two variables="+ remainder);
		
		

	}

}
