package Redowork;

public class IncrementOrDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int d=60;
		int s=d++;
		    /*instead of writing d=d+1; we can write d++;
		d++;in sysout it increase the value by 1(61)
		d--; in sysout it will check and decrease the value by 1(60)*/
		System.out.println("This is the value of d="+d);    
		System.out.println("This is the value of s="+s);
		//s is fetching the value of d before adding, d is printing it after adding
		

	}

}
